import react, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useSelector,useDispatch } from 'react-redux';
import store, { addItem } from './../store';



function Detail({pang, chief}){

  useEffect(()=>{
    let storage = JSON.parse(sessionStorage.getItem('watched'));
    storage.push(pang);
    storage = new Set(storage); //Î∞∞Ïó¥Ïùò Ï§ëÎ≥µÏùÑ Ï†úÍ±∞
    storage = Array.from(storage); //Îã§Ïãú Î∞∞Ïó¥Ìôî
    sessionStorage.setItem('watched', JSON.stringify(storage));

    let timer = setTimeout(()=>{ setNotice(false) },10000); 
    return ()=>{//useEffectÎ≥¥Îã§ Î®ºÏ†Ä Ïã§Ìñâ
      clearTimeout(timer);
    }
  },[]);

  let {id} = useParams();//Ïú†Ï†ÄÍ∞Ä urlÌååÎùºÎØ∏ÌÑ∞Ïóê ÏûÖÎ†•Ìïú Í≤ÉÏùÑ Í∞ÄÏ†∏Ïò§Î†§Î©¥ useParams()
  let idSynchro = pang.find((pang)=>{
    return pang.id == id
  });
  let [notice,setNotice] = useState(true);
  let [countNum, setCountNum] = useState(1);
  let [totalPrice, setTotalPrice] = useState(idSynchro.price);
  let [index, setIndex] = useState(0);

  let dispatch = useDispatch();

  return (
    <div className="Detail">
      <h2 className='componentHead'>Ï†úÌíàÏÉÅÏÑ∏</h2>
      <div className="upDetail">
        <div className="imgContainer">
          <img className="detailImage" src={process.env.PUBLIC_URL + pang[id].image}/>
        </div>
        <div className="description">
          <h1>{idSynchro.title}</h1>
          <h4>{idSynchro.content}</h4>
          <p>ÌåêÎß§Í∞ÄÍ≤©: {idSynchro.price.toLocaleString('ko-KR')}Ïõê</p>
          <p className="desLine desLine1"></p>
          <h4 className="allergy">ÏïåÎ†àÎ•¥Í∏∞ Ï†ïÎ≥¥</h4>
          <p>Î≥∏ Ï†úÌíàÏùÄ {idSynchro.allergy} Ìï®Ïú† Ï†úÌíàÏûÖÎãàÎã§.</p>
          <p className="desLine desLine2"></p>
          <button className='plusCount' onClick={()=>{ //Ïò¨Î¶ºÎ≤ÑÌäº
            ++ countNum;
            setCountNum(countNum);
            let plusPrice = countNum * idSynchro.price;
            setTotalPrice(plusPrice);
          }}> ‚Üë </button> 
          <span className='count'>{countNum}Í∞ú</span> {/*Í∞ØÏàò*/}
          <button className='minusCount' onClick={()=>{ //ÎÇ¥Î¶ºÎ≤ÑÌäº
            if(countNum > 1){
              let plusPrice = countNum * idSynchro.price;
              let minusPrice = plusPrice - idSynchro.price;
              setTotalPrice(minusPrice);
              -- countNum;
              setCountNum(countNum);
            }
            else if(countNum = 1){
              return 1;
            }
          }}> ‚Üì </button>
          <span className='totalPrice'>Í∏àÏï°: {totalPrice.toLocaleString('ko-KR')}Ïõê</span> {/* Îã¥ÏùÄÍ∏àÏï° */}
          <p>
            <button className="orderBtn" onClick={() => { //Ïû•Î∞îÍµ¨ÎãàÏóê Îã¥Í∏∞ Î≤ÑÌäº
              if(countNum >= 1){
                if(index == 1){
                return alert('Í∏∞Ï°¥ÏÉÅÌíàÏù¥ Ïù¥ÎØ∏ Ïû•Î∞îÍµ¨Îãà ÏïàÏóê ÏûàÏäµÎãàÎã§.')
                }
                else if(index == 0){
                dispatch(addItem( {id: `${idSynchro.id}`, title: `${idSynchro.title}`, count: countNum, price: `${idSynchro.price}`,totalPrice: totalPrice, image: `${idSynchro.image}`}));
                alert('ÏÉÅÌíàÏù¥ Ïû•Î∞îÍµ¨ÎãàÏóê Îã¥Í≤ºÏäµÎãàÎã§.');
                setIndex(1);
                }
              }
            }}>Ïû•Î∞îÍµ¨Îãà</button>
          </p>
          {
            notice == true ?
            <p className="notice">üéÅ Í≥ß ÏÇ¨ÎùºÏ†∏Ïöî~! ÏßÄÍ∏à Ï£ºÎ¨∏Ïãú ÏÇ¨ÏùÄÌíàÌè¨Ìï® <span>Î∞∞ÏÜ°!</span></p>
            : <p className="noticeOff">üéÅ Í≥ß ÏÇ¨ÎùºÏ†∏Ïöî~ ÏßÄÍ∏à Ï£ºÎ¨∏Ïãú ÏÇ¨ÏùÄÌíàÌè¨Ìï® <span>Î≤†ÏÜ°!</span></p>
          }
        </div>
      </div>
      <div className="underDetail">
        <div className="review">
          <h2>ÏõêÏÇ∞ÏßÄ Ï†ïÎ≥¥Î•º ÌôïÏù∏ÌïòÏÑ∏Ïöî.</h2>
          <p>{idSynchro.resource}</p>
        </div>
        <div className="nutrition">
          <table>
            <thead>
              <tr>
                <th className='leftNutriTitle'>1Ìöå ÏòÅÏñëÏÑ±Î∂Ñ</th>
                <th className='rightNutriTitle'>100gÎãπ</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <th className='leftNutri'>ÏπºÎ°úÎ¶¨</th>
                <th className='rightNutri'>{idSynchro.kcal}kcal</th>
              </tr>
              <tr>
                <th className='leftNutri'>ÎÇòÌä∏Î•®</th>
                <th className='rightNutri'>{idSynchro.sodium}mg</th>
              </tr>
              <tr>
                <th className='leftNutri'>ÎãπÎ•ò</th>
                <th className='rightNutri'>{idSynchro.sugars}g</th>
              </tr>
              <tr>
                <th className='leftNutri'>Ìè¨ÌôîÏßÄÎ∞©</th>
                <th className='rightNutri'>{idSynchro.fat}g</th>
              </tr>
